INSERT INTO `choice_encounters` (`id`, `name`, `text`, `c_choice_1`, `c_id_1`, `c_type_1`, `c_artifact_required_1`, `c_choice_2`, `c_id_2`, `c_type_2`, `c_choice_3`, `c_id_3`, `c_type_3`) VALUES
(1, 'Fighting some guys!', 'Wow, here is some text, and now you can fight some guys!', 'Armoured Kobold', 4, 1, 0, 'Aged Boar', 85, 1, 'Lieutenant Szoke', 151, 1),
(2, 'Distracted Crabber', 'As you scout along the shoreline, you notice a crabber fooling around with some gear.  The trap that they''ve set is floating on the water, and looks like it''s about to drift out of your grasp.', 'Grab the Crab Trap, and get out of here!', 34, 2, 0, 'Attack the Pravokan Crabber!', 215, 1, 'Leave, and do nothing', 35, 2),
(3, 'An Unwatched Pile', 'As you walk around the coastline, you notice a Pravokan Lookout scouting the area for enemies.  You manage to sneak in closer, and notice a stray piece of their outfit laying on the ground.', 'Attack the Pravokan Lookout', 217, 1, 0, 'Grab the Pravokan Belt', 36, 2, 'Make a hasty retreat!', 35, 2),
(4, 'Some Spare Goods', 'You stumble across a Pravokan Oarsman lounging around and making the most of his leisure time.  As you cautiously glance around, you realize that a pair of bracers aren''t too far out of your reach.', 'Attack the Pravokan Oarsman', 216, 1, 0, 'Grab the Pravokan Bracers', 37, 2, 'Make a hasty retreat!', 35, 2),
(5, 'Red Berries In Sight', 'As you patrol the area, you notice Pravokan Trainee hovering nearby a Wild Strawberry patch.  You bide your time, while deciding what to do.', 'Attack the Pravokan Trainee', 219, 1, 0, 'Take the Wild Strawberry', 38, 2, 'Leave, and do nothing', 35, 2),
(6, 'Violet Flowers In Sight', 'You stumble across a Pravokan Supervisor working next to a Violet Starleaf!  You bide your time, while deciding what to do.', 'Attack the Pravokan Supervisor', 220, 1, 0, 'Take the Violet Starleaf', 39, 2, 'Leave, and do nothing', 35, 2),
(7, 'Yellow Flowers In Sight', 'As you navigate around the area, you stumble upon a Pravokan Swordsmith taking a small rest.  Nearby rests a Yellow Trillium, growing out of the ground.  You bide your time, while deciding what to do.', 'Attack the Pravokan Swordsmith', 221, 1, 0, 'Take the Yellow Trillium', 40, 2, 'Leave, and do nothing', 35, 2),
(8, 'A Batch of Timber', 'As you patrol the island, you stumble onto a guarded pile of Eldras Timber!  There is a Pravokan standing nearby, but if you wait, you can probably sneak off with the load.', 'Attack the Pravokan Guardian', 223, 1, 0, 'Grab the Pravokan Timber', 41, 2, 'Leave, and do nothing', 35, 2),
(9, 'A Batch of Timber', 'As you patrol the island, you stumble onto a guarded pile of Eldras Timber!  There is a Pravokan standing nearby, but if you wait, you can probably sneak off with the load.', 'Attack the Pravokan Spellcrafter', 224, 1, 0, 'Grab the Pravokan Timber', 41, 2, 'Leave, and do nothing', 35, 2),
(10, 'A Batch of Timber', 'As you patrol the island, you stumble onto a guarded pile of Eldras Timber!  There is a Pravokan standing nearby, but if you wait, you can probably sneak off with the load.', 'Attack the Pravokan Shipbuilder', 225, 1, 0, 'Grab the Pravokan Timber', 41, 2, 'Leave, and do nothing', 35, 2),
(11, 'A large pile of gold?', 'It looks as though you''ve tracked down a group of kobolds guarding some gold!  You think you might be able to sneak in to steal the treasure without being caught.  What do you do?', 'Grab the gold!', 1, 2, 0, 'Leave, and do nothing', 35, 2, '', 0, 0),
(12, 'A Fork In The Path', 'As you navigate your way through the winding passages, you come across a three-way split.  With the defending forces rallying behind you, a hasty decision is needed as to which path you''ll now take!', 'Take the Left Treasury Path', 43, 2, 0, 'Take the Center Treasury Path', 44, 2, 'Take the Right Treasury Path', 45, 2),
(13, 'The Labyrinth''s Doorway', 'As you approach the Great Labyrinth, you are stunned to see a massive stone structure standing in your path.  In order to enter the halls, it appears you''ll need a Great Labyrinth Key.', 'Use your Great Labyrinth Key', 52, 2, 824, 'Do nothing', 53, 2, '', 0, 0),
(14, 'The Great Labyrinth', 'You stand in the center of a large stone room with three passages to follow.  A strange ambient light fills the room, and a sparse collection of burnt-out torches are hung across the walls.  Strange piles of mechanical goods are strewn across the floor, although it all looks overused and not salvageable.  A single coarse line is etched into the wall.', 'Take the north passage', 7, 4, 0, 'Take the east passage', 4, 4, 'Take the south passage', 0, 4),
