INSERT INTO `foes` (`id`, `name`, `text`, `creature_type`, `hp`, `xp`, `level`, `armour`, `str`, `dex`, `int`, `cha`, `con`, `base_gold`, `random_gold`, `attack_resistance`, `attack_resistance_amount`, `attack_penetration`, `attack_vulnerable`, `special`, `reputation_id`, `reputation_value`, `reputation_max_award`) VALUES
(1, 'Sleepy Kobold', 'You have stumbled on to a sleepy looking kobold!  It doesn''t appear to be much of a threat, but you prepare to defend yourself.', 1, 5, 5, 1, 0, 9, 6, 5, 5, 8, 1, 6, 0, 0, 0, 0, 0, 0, 0, 1),
(2, 'Enraged Kobold', 'You walk in to the path of an angry looking kobold!  He doesn''t look happy to see you, and charges!', 1, 5, 5, 1, 25, 10, 6, 5, 5, 9, 1, 10, 0, 0, 0, 0, 0, 0, 0, 1),
(3, 'Rabid Squirrel', 'An adorable looking squirrel, except for the streams of foam shooting from a diseased mouth.  Ew!  It looks pissed.', 2, 3, 5, 1, 0, 6, 9, 3, 4, 4, 2, 12, 0, 0, 0, 0, 0, 0, 0, 1),
(4, 'Armoured Kobold', 'This kobold appears set up to defend their shoddy living quarters.  He wears a thin set of weak leather armour.', 1, 5, 5, 1, 100, 10, 5, 5, 5, 10, 1, 11, 0, 0, 0, 0, 0, 0, 0, 1),
(5, 'Kobold Scout', 'This younger kobold appears to have been tasked with keeping an eye out on the camp.  When you''re spotted, it seems to freeze with fear, and you take advantage of the situation with an attack!', 1, 5, 5, 1, 20, 9, 5, 5, 5, 8, 1, 9, 0, 0, 0, 0, 0, 0, 0, 1),
(6, 'Scrawny Zombie', 'This pathetic looking zombie saunters towards you at a very slow speed.  It looks quite thin, which means it''s probably quite hungry.', 3, 8, 10, 2, 60, 11, 4, 2, 3, 13, 2, 12, 0, 0, 0, 65536, 0, 2, 1000, 50000),
(7, 'Wretched Zombie', 'This zombie appears to have undergone several years of decay, and moves slowly, but assuredly.  It appears to want to eat you.  Hmm.', 3, 8, 10, 2, 150, 13, 4, 2, 2, 14, 19, 6, 0, 0, 0, 65536, 0, 2, 1000, 50000),
(8, 'Blind Zombie', 'As if zombies weren''t wretched enough, this one seems to have no eyes.  They might have rotted away months ago, but it seems to hunger after you based on sound and smell alone.', 3, 6, 10, 2, 190, 11, 4, 2, 3, 14, 2, 8, 0, 0, 0, 65536, 0, 2, 1000, 50000),
(9, 'Iron Hand Rebel Scout', 'You''ve been seen by a young soldier on his patrol!  He looks wary, but lunges at you in attack!', 1, 13, 10, 2, 220, 12, 8, 5, 8, 11, 14, 13, 0, 0, 0, 0, 0, 1, 1000, 250000),
(10, 'Iron Hand Rebel Soldier', 'This armoured foe steps at you, intending to remove you from the camp!', 1, 15, 10, 2, 250, 13, 9, 5, 8, 11, 14, 13, 0, 0, 0, 0, 0, 1, 1000, 250000),
(11, 'Iron Hand Rebel Captain', 'You''ve fallen under the gaze of one of the camp captains!  He grins, cracks his knuckles, and steps towards you!', 1, 16, 10, 2, 365, 11, 11, 5, 8, 12, 12, 17, 0, 0, 0, 0, 0, 1, 3000, 250000),
(12, 'Young Brown Bear', 'Though it''s just a cub, it''s still quite large and threatening!  It rears itself up on its hind legs, and growls at you!', 2, 25, 20, 4, 425, 11, 10, 6, 5, 13, 15, 45, 0, 0, 0, 0, 0, 0, 0, 1),
(13, 'Young Grey Bear', 'Though it''s just a cub, it''s still quite large and threatening!  It rears itself up on its hind legs, and growls at you!', 2, 25, 20, 4, 425, 11, 10, 6, 5, 13, 15, 45, 0, 0, 0, 0, 0, 0, 0, 1),
(14, 'Young Black Bear', 'Though it''s just a cub, it''s still quite large and threatening!  It rears itself up on its hind legs, and growls at you!', 2, 25, 20, 4, 425, 11, 10, 6, 5, 13, 15, 45, 0, 0, 0, 0, 0, 0, 0, 1),
(15, 'Adult Brown Bear', 'The large bear dwarfs you in size, and appears unimpressed that you''ve dared to enter its lair!', 2, 28, 25, 5, 550, 13, 11, 7, 6, 14, 35, 15, 0, 0, 0, 0, 0, 0, 0, 1),
(16, 'Adult Grey Bear', 'The large bear dwarfs you in size, and appears unimpressed that you''ve dared to enter its lair!', 2, 28, 25, 5, 550, 13, 11, 7, 6, 14, 35, 15, 0, 0, 0, 0, 0, 0, 0, 1),
